import{_ as s,c as n,o as a,a as e}from"./app.526dca4e.js";const _=JSON.parse('{"title":"Linux \u7F51\u5361\u591A\u961F\u5217\u4ECB\u7ECD","description":"","frontmatter":{"title":"Linux \u7F51\u5361\u591A\u961F\u5217\u4ECB\u7ECD","author":"Peter Wang","tags":["RSS","\u591A\u7F51\u5361\u961F\u5217"],"date":"2019-04-07T02:05:29.000Z","draft":false},"headers":[{"level":3,"title":"\u591A\u7F51\u5361\u961F\u5217\u5B9E\u73B0\u56FE\u4F8B","slug":"\u591A\u7F51\u5361\u961F\u5217\u5B9E\u73B0\u56FE\u4F8B"},{"level":3,"title":"\u68C0\u67E5\u4E2D\u65AD\u4E0E\u5BF9\u5E94\u7684CPU\u5173\u7CFB","slug":"\u68C0\u67E5\u4E2D\u65AD\u4E0E\u5BF9\u5E94\u7684cpu\u5173\u7CFB"},{"level":3,"title":"RPS, XPS, RFS","slug":"rps-xps-rfs"},{"level":3,"title":"\u6839\u636Etop\u8F93\u51FA\u67E5\u770B\u8F6F\u4E2D\u65AD\u8D1F\u8F7D","slug":"\u6839\u636Etop\u8F93\u51FA\u67E5\u770B\u8F6F\u4E2D\u65AD\u8D1F\u8F7D"},{"level":3,"title":"\u53C2\u8003","slug":"\u53C2\u8003"}],"relativePath":"linux/multi-queue.md","lastUpdated":1657810449000}'),p={name:"linux/multi-queue.md"},l=e(`<p>\u5355CPU\u5904\u7406\u7F51\u7EDCIO\u5B58\u5728\u74F6\u9888, \u76EE\u524D\u7ECF\u5E38\u4F7F\u7528\u7F51\u5361\u591A\u961F\u5217\u63D0\u9AD8\u6027\u80FD.</p><p>\u901A\u5E38\u60C5\u51B5\u4E0B, \u6BCF\u5F20\u7F51\u5361\u6709\u4E00\u4E2A\u961F\u5217(queue), \u6240\u6709\u6536\u5230\u7684\u5305\u4ECE\u8FD9\u4E2A\u961F\u5217\u5165, \u5185\u6838\u4ECE\u8FD9\u4E2A\u961F\u5217\u91CC\u53D6\u6570\u636E\u5904\u7406. \u8BE5\u961F\u5217\u5176\u5B9E\u662Fring buffer(\u73AF\u5F62\u961F\u5217), \u5185\u6838\u5982\u679C\u53D6\u6570\u636E\u4E0D\u53CA\u65F6, \u5219\u4F1A\u5B58\u5728\u4E22\u5305\u7684\u60C5\u51B5.<br> \u4E00\u4E2ACPU\u5904\u7406\u4E00\u4E2A\u961F\u5217\u7684\u6570\u636E, \u8FD9\u4E2A\u53EB\u4E2D\u65AD. \u9ED8\u8BA4\u662Fcpu0(\u7B2C\u4E00\u4E2ACPU)\u5904\u7406. \u4E00\u65E6\u6D41\u91CF\u7279\u522B\u5927, \u8FD9\u4E2ACPU\u8D1F\u8F7D\u5F88\u9AD8, \u6027\u80FD\u5B58\u5728\u74F6\u9888. \u6240\u4EE5\u7F51\u5361\u5F00\u53D1\u4E86\u591A\u961F\u5217\u529F\u80FD, \u5373\u4E00\u4E2A\u7F51\u5361\u6709\u591A\u4E2A\u961F\u5217, \u6536\u5230\u7684\u5305\u6839\u636ETCP\u56DB\u5143\u7EC4\u4FE1\u606Fhash\u540E\u653E\u5165\u5176\u4E2D\u4E00\u4E2A\u961F\u5217, \u540E\u9762\u8BE5\u94FE\u63A5\u7684\u6240\u6709\u5305\u90FD\u653E\u5165\u8BE5\u961F\u5217. \u6BCF\u4E2A\u961F\u5217\u5BF9\u5E94\u4E0D\u540C\u7684\u4E2D\u65AD, \u4F7F\u7528irqbalance\u5C06\u4E0D\u540C\u7684\u4E2D\u65AD\u7ED1\u5B9A\u5230\u4E0D\u540C\u7684\u6838. \u5145\u5206\u5229\u7528\u4E86\u591A\u6838\u5E76\u884C\u5904\u7406\u7279\u6027. \u63D0\u9AD8\u4E86\u6548\u7387.</p><h3 id="\u591A\u7F51\u5361\u961F\u5217\u5B9E\u73B0\u56FE\u4F8B" tabindex="-1">\u591A\u7F51\u5361\u961F\u5217\u5B9E\u73B0\u56FE\u4F8B <a class="header-anchor" href="#\u591A\u7F51\u5361\u961F\u5217\u5B9E\u73B0\u56FE\u4F8B" aria-hidden="true">#</a></h3><div class="language-"><span class="copy"></span><pre><code><span class="line"><span style="color:#A6ACCD;">             \u666E\u901A\u5355\u961F\u5217                                   </span></span>
<span class="line"><span style="color:#A6ACCD;">   +-----------------------------+                        </span></span>
<span class="line"><span style="color:#A6ACCD;">   | queue                       |                        </span></span>
<span class="line"><span style="color:#A6ACCD;">   |                             |                        </span></span>
<span class="line"><span style="color:#A6ACCD;">   |   +----------+  +----------+|           +---------+  </span></span>
<span class="line"><span style="color:#A6ACCD;">   |   |  packet  |  |  packet  ||----------&gt;|  CPU 0  |  </span></span>
<span class="line"><span style="color:#A6ACCD;">   |   +----------+  +----------+|           +---------+  </span></span>
<span class="line"><span style="color:#A6ACCD;">   +-----------------------------+                        </span></span>
<span class="line"><span style="color:#A6ACCD;">                                                    </span></span>
<span class="line"><span style="color:#A6ACCD;">                             \u5F00\u542F\u591A\u7F51\u5361\u961F\u5217               </span></span>
<span class="line"><span style="color:#A6ACCD;">                                                        </span></span>
<span class="line"><span style="color:#A6ACCD;">    +----------------------------+                       </span></span>
<span class="line"><span style="color:#A6ACCD;">    | queue                      |                       </span></span>
<span class="line"><span style="color:#A6ACCD;">    |                            |                       </span></span>
<span class="line"><span style="color:#A6ACCD;">    |  +----------+ +----------+ |           +---------+ </span></span>
<span class="line"><span style="color:#A6ACCD;">    |  |  packet  | |  packet  | |---------&gt; |  CPU 0  | </span></span>
<span class="line"><span style="color:#A6ACCD;">    |  +----------+ +----------+ |           +---------+ </span></span>
<span class="line"><span style="color:#A6ACCD;">    +----------------------------+           +---------+ </span></span>
<span class="line"><span style="color:#A6ACCD;">                                             |  CPU 1  |  </span></span>
<span class="line"><span style="color:#A6ACCD;">                                             +---------+  </span></span>
<span class="line"><span style="color:#A6ACCD;">                                             +---------+  </span></span>
<span class="line"><span style="color:#A6ACCD;">    +----------------------------+           |  CPU 2  |  </span></span>
<span class="line"><span style="color:#A6ACCD;">    | queue                      |           +---------+  </span></span>
<span class="line"><span style="color:#A6ACCD;">    |                            |                        </span></span>
<span class="line"><span style="color:#A6ACCD;">    |  +----------+ +----------+ |           +---------+  </span></span>
<span class="line"><span style="color:#A6ACCD;">    |  |  packet  | |  packet  | |---------&gt; |  CPU 3  |  </span></span>
<span class="line"><span style="color:#A6ACCD;">    |  +----------+ +----------+ |           +---------+  </span></span>
<span class="line"><span style="color:#A6ACCD;">    +----------------------------+                        </span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><h3 id="\u68C0\u67E5\u4E2D\u65AD\u4E0E\u5BF9\u5E94\u7684cpu\u5173\u7CFB" tabindex="-1">\u68C0\u67E5\u4E2D\u65AD\u4E0E\u5BF9\u5E94\u7684CPU\u5173\u7CFB <a class="header-anchor" href="#\u68C0\u67E5\u4E2D\u65AD\u4E0E\u5BF9\u5E94\u7684cpu\u5173\u7CFB" aria-hidden="true">#</a></h3><p>\u5982\u4E0B\u663E\u793A, \u7B2C\u4E00\u5217\u662F\u4E2D\u65AD\u53F7, \u540E\u9762\u4E24\u5217\u662F\u5BF9\u5E94CPU\u5904\u7406\u8BE5\u4E2D\u65AD\u7684\u6B21\u6570, virtio-input\u548C virtio-output\u4E3A\u7F51\u5361\u961F\u5217\u7684\u4E2D\u65AD \u53EF\u89C1\u5927\u90E8\u5206\u5305\u88ABCPU1\u5904\u7406</p><div class="language-bash"><span class="copy"></span><pre><code><span class="line"><span style="color:#676E95;font-style:italic;"># cat /proc/interrupts | egrep &#39;CPU|virtio.*(input|output)&#39;</span></span>
<span class="line"><span style="color:#A6ACCD;">           CPU0       CPU1</span></span>
<span class="line"><span style="color:#A6ACCD;"> 27:          7      89632   PCI-MSI-edge      virtio3-input.0</span></span>
<span class="line"><span style="color:#A6ACCD;"> 30:          2          0   PCI-MSI-edge      virtio3-output.0</span></span>
<span class="line"><span style="color:#A6ACCD;"> 31:          7      23319   PCI-MSI-edge      virtio3-input.1</span></span>
<span class="line"><span style="color:#A6ACCD;"> 32:          2          0   PCI-MSI-edge      virtio3-output.1</span></span>
<span class="line"></span></code></pre></div><p>\u67E5\u8BE2\u5177\u4F53\u4E2D\u65AD\u6240\u7ED1\u5B9A\u7684CPU\u4FE1\u606F<br> smp_affinity_list\u663E\u793ACPU\u5E8F\u53F7. \u6BD4\u5982 0 \u4EE3\u8868 CPU0, 2\u4EE3\u8868 CPU2 smp_affinity \u662F\u5341\u516D\u8FDB\u5236\u663E\u793A. \u6BD4\u5982 2 \u4E3A10, \u4EE3\u8868 CPU1 (\u7B2C\u4E8C\u4E2ACPU)</p><div class="language-bash"><span class="copy"></span><pre><code><span class="line"><span style="color:#676E95;font-style:italic;"># for i in {30..32}; do echo -n &quot;Interrupt $i is allowed on CPUs &quot;; cat /proc/irq/$i/smp_affinity_list; done</span></span>
<span class="line"><span style="color:#A6ACCD;">Interrupt 30 is allowed on CPUs 0</span></span>
<span class="line"><span style="color:#A6ACCD;">Interrupt 31 is allowed on CPUs 1</span></span>
<span class="line"><span style="color:#A6ACCD;">Interrupt 32 is allowed on CPUs 0</span></span>
<span class="line"></span></code></pre></div><h3 id="rps-xps-rfs" tabindex="-1">RPS, XPS, RFS <a class="header-anchor" href="#rps-xps-rfs" aria-hidden="true">#</a></h3><p>\u4E4B\u524D\u8C08\u7684\u591A\u7F51\u5361\u961F\u5217\u9700\u8981\u786C\u4EF6\u5B9E\u73B0, RPS\u5219\u662F\u8F6F\u4EF6\u5B9E\u73B0,\u5C06\u5305\u8BA9\u6307\u5B9A\u7684CPU\u53BB\u5904\u7406\u4E2D\u65AD.<br> \u914D\u7F6E\u6587\u4EF6\u4E3A<code>/sys/class/net/eth*/queues/rx*/rps_cpus</code>. \u9ED8\u8BA4\u4E3A0, \u8868\u793A\u4E0D\u542F\u52A8RPS \u5982\u679C\u8981\u8BA9\u8BE5\u961F\u5217\u88ABCPU0,1\u5904\u7406, \u5219\u8BBE\u7F6E echo &quot;3&quot; &gt; /sys/class/net/eth*/queues/rx*/rps_cpus, 3\u4EE3\u8868\u5341\u516D\u8FDB\u5236\u8868\u793A11, \u5373\u6307CPU0\u548CCPU1<br> \u5728\u5F00\u542F\u591A\u7F51\u5361\u961F\u5217RSS\u65F6, \u5DF2\u7ECF\u8D77\u5230\u4E86\u5747\u8861\u7684\u4F5C\u7528. RPS\u5219\u53EF\u4EE5\u5728\u961F\u5217\u6570\u5C0F\u4E8ECPU\u6570\u65F6, \u8FDB\u4E00\u6B65\u63D0\u5347\u6027\u80FD. \u56E0\u4E3A\u8FDB\u4E00\u6B65\u5229\u7528\u6240\u6709CPU. RFS\u5219\u8FDB\u4E00\u6B65\u6269\u5C55RPS\u7684\u80FD\u529B, \u5B83\u4F1A\u5206\u6790\u5E76\u5C06\u5305\u53D1\u5F80\u6700\u5408\u9002\u7684CPU(\u7A0B\u5E8F\u8FD0\u884C\u6240\u5728\u7684CPU). \u68C0\u67E5\u5F53\u524DRPS, RFS\u5F00\u542F\u60C5\u51B5:</p><div class="language-bash"><span class="copy"></span><pre><code><span class="line"><span style="color:#676E95;font-style:italic;"># for i in $(ls -1 /sys/class/net/eth*/queues/rx*/rps_*); do echo -n &quot;\${i}:  &quot;  ; cat \${i}; done</span></span>
<span class="line"><span style="color:#A6ACCD;">/sys/class/net/eth0/queues/rx-0/rps_cpus:  3</span></span>
<span class="line"><span style="color:#A6ACCD;">/sys/class/net/eth0/queues/rx-0/rps_flow_cnt:  4096</span></span>
<span class="line"><span style="color:#A6ACCD;">/sys/class/net/eth0/queues/rx-1/rps_cpus:  3</span></span>
<span class="line"><span style="color:#A6ACCD;">/sys/class/net/eth0/queues/rx-1/rps_flow_cnt:  4096</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># cat /proc/sys/net/core/rps_sock_flow_entries</span></span>
<span class="line"><span style="color:#A6ACCD;">8192</span></span>
<span class="line"></span></code></pre></div><p>XPS\u662F\u5C06\u53D1\u9001\u5305\u6307\u5B9A\u5230CPU, \u901A\u5E38\u548C\u540C\u4E00\u961F\u5217\u7684rps\u548Cxps\u914D\u7F6E\u4E00\u81F4.</p><div class="language-bash"><span class="copy"></span><pre><code><span class="line"><span style="color:#676E95;font-style:italic;"># for i in $(ls -1 /sys/class/net/eth*/queues/tx*/xps_cpus); do echo -n &quot;\${i}:  &quot;  ; cat \${i}; done</span></span>
<span class="line"><span style="color:#A6ACCD;">/sys/class/net/eth0/queues/tx-0/xps_cpus:  3</span></span>
<span class="line"><span style="color:#A6ACCD;">/sys/class/net/eth0/queues/tx-1/xps_cpus:  3</span></span>
<span class="line"></span></code></pre></div><h3 id="\u6839\u636Etop\u8F93\u51FA\u67E5\u770B\u8F6F\u4E2D\u65AD\u8D1F\u8F7D" tabindex="-1">\u6839\u636Etop\u8F93\u51FA\u67E5\u770B\u8F6F\u4E2D\u65AD\u8D1F\u8F7D <a class="header-anchor" href="#\u6839\u636Etop\u8F93\u51FA\u67E5\u770B\u8F6F\u4E2D\u65AD\u8D1F\u8F7D" aria-hidden="true">#</a></h3><p>top\u8FDB\u5165\u4EA4\u4E92\u5F0F\u754C\u9762\u540E, \u63091 \u663E\u793A\u6240\u6709cpu\u7684\u8D1F\u8F7D. si \u662F\u8F6F\u4E2D\u65AD\u7684CPU\u4F7F\u7528\u7387. \u5982\u679C\u9AD8\u6BD4\u598250%, \u8BF4\u660E\u8BE5CPU\u5FD9\u4E8E\u5904\u7406\u4E2D\u65AD, \u901A\u5E38\u5C31\u662F\u6536\u53D1\u7F51\u7EDCIO</p><div class="language-"><span class="copy"></span><pre><code><span class="line"><span style="color:#A6ACCD;">top - 18:58:33 up 16 days, 19:58,  2 users,  load average: 0.00, 0.01, 0.05</span></span>
<span class="line"><span style="color:#A6ACCD;">Tasks:  89 total,   2 running,  87 sleeping,   0 stopped,   0 zombie</span></span>
<span class="line"><span style="color:#A6ACCD;">%Cpu0  :  1.3 us,  0.0 sy,  0.0 ni, 98.7 id,  0.0 wa,  0.0 hi,  0.0 si,  0.0 st</span></span>
<span class="line"><span style="color:#A6ACCD;">%Cpu1  :  0.0 us,  0.0 sy,  0.0 ni,100.0 id,  0.0 wa,  0.0 hi,  0.0 si,  0.0 st</span></span>
<span class="line"><span style="color:#A6ACCD;">KiB Mem :  3880032 total,  2911912 free,   199600 used,   768520 buff/cache</span></span>
<span class="line"><span style="color:#A6ACCD;">KiB Swap:        0 total,        0 free,        0 used.  3411892 avail Mem</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><h3 id="\u53C2\u8003" tabindex="-1">\u53C2\u8003 <a class="header-anchor" href="#\u53C2\u8003" aria-hidden="true">#</a></h3><p><a href="https://www.kernel.org/doc/Documentation/IRQ-affinity.txt" target="_blank" rel="noopener noreferrer">https://www.kernel.org/doc/Documentation/IRQ-affinity.txt</a><br><a href="https://www.kernel.org/doc/Documentation/networking/scaling.txt" target="_blank" rel="noopener noreferrer">https://www.kernel.org/doc/Documentation/networking/scaling.txt</a><br><a href="https://access.redhat.com/documentation/en-us/red_hat_enterprise_linux/7/html/performance_tuning_guide/sect-red_hat_enterprise_linux-performance_tuning_guide-networking-configuration_tools#sect-Red_Hat_Enterprise_Linux-Performance_Tuning_Guide-Configuration_tools-Configuring_Receive_Packet_Steering_RPS" target="_blank" rel="noopener noreferrer">https://access.redhat.com/documentation/en-us/red_hat_enterprise_linux/7/html/performance_tuning_guide/sect-red_hat_enterprise_linux-performance_tuning_guide-networking-configuration_tools#sect-Red_Hat_Enterprise_Linux-Performance_Tuning_Guide-Configuration_tools-Configuring_Receive_Packet_Steering_RPS</a><br><a href="https://lwn.net/Articles/370153/" target="_blank" rel="noopener noreferrer">https://lwn.net/Articles/370153/</a><br><a href="https://lwn.net/Articles/412062/" target="_blank" rel="noopener noreferrer">https://lwn.net/Articles/412062/</a></p>`,19),t=[l];function o(r,c,i,C,u,A){return a(),n("div",null,t)}var y=s(p,[["render",o]]);export{_ as __pageData,y as default};
