import{_ as e,c as t,o as r,a as n}from"./app.526dca4e.js";const g=JSON.parse('{"title":"TCP\u534F\u8BAE\u6808\u7B14\u8BB0","description":"","frontmatter":{"title":"TCP\u534F\u8BAE\u6808\u7B14\u8BB0","author":"Peter Wang","tags":["tcp","net stack"],"date":"2019-03-24T06:31:29.000Z","draft":false},"headers":[{"level":3,"title":"TCP\u5904\u7406\u4E0E\u5185\u6838\u53C2\u6570","slug":"tcp\u5904\u7406\u4E0E\u5185\u6838\u53C2\u6570"},{"level":3,"title":"TCP\u534A\u961F\u5217\u548C\u5168\u961F\u5217\u76D1\u63A7","slug":"tcp\u534A\u961F\u5217\u548C\u5168\u961F\u5217\u76D1\u63A7"},{"level":3,"title":"TCP\u4E22\u5305\u4E00\u4E9B\u6392\u67E5\u65B9\u6CD5","slug":"tcp\u4E22\u5305\u4E00\u4E9B\u6392\u67E5\u65B9\u6CD5"},{"level":3,"title":"net.ipv4.tcptwrecycle = 1","slug":"net-ipv4-tcp-tw-recycle-1"},{"level":3,"title":"tcp\u90E8\u5206\u53C2\u6570\u4F7F\u7528\u4ECB\u7ECD","slug":"tcp\u90E8\u5206\u53C2\u6570\u4F7F\u7528\u4ECB\u7ECD"}],"relativePath":"network/tcp.md","lastUpdated":1657810449000}'),a={name:"network/tcp.md"},c=n(`<p>\u8BB0\u5F55\u81EA\u5DF1\u5BF9TCP\u534F\u8BAE\u6808\u7684\u5B66\u4E60\u5FC3\u5F97</p><h3 id="tcp\u5904\u7406\u4E0E\u5185\u6838\u53C2\u6570" tabindex="-1">TCP\u5904\u7406\u4E0E\u5185\u6838\u53C2\u6570 <a class="header-anchor" href="#tcp\u5904\u7406\u4E0E\u5185\u6838\u53C2\u6570" aria-hidden="true">#</a></h3><ol><li>client\u53D1\u9001<code>syn</code>\u62A5\u6587\u540E,tcp\u72B6\u6001\u4E3A syn-sent. \u5982\u679C\u5BF9\u7AEF\u6CA1\u6709\u8FD4\u56DEsyn+ack, \u5219\u5C1D\u8BD5\u53D1\u9001<code>net.ipv4.tcp_syn_retries</code>\u6B21. \u6BCF\u6B21\u95F4\u96942,4,8s....</li><li>server\u6536\u5230<code>syn</code>\u540E,\u72B6\u6001\u53D8\u4E3A syn-recv, \u653E\u5230\u534A\u961F\u5217\u91CC,\u540C\u65F6\u8FD4\u56DEsyn-ack. \u8FD9\u4E2A\u961F\u5217\u7684\u5927\u5C0F\u4E3A net.ipv4.tcp_max_syn_backlog. \u662F\u4E2A\u5168\u5C40\u7684\u53C2\u6570. \u5982\u679C\u8FD9\u4E2A\u534A\u961F\u5217\u6EE1,\u5219\u76F4\u63A5\u4E22\u5F03syc.</li><li>\u5F53server\u6536\u5230ack\u540E, \u4F1A\u68C0\u67E5\u5168\u961F\u5217\u662F\u5426\u80FD\u653E\u4E0B. \u8FD9\u4E2A\u5927\u5C0F\u4E3A min(backlog, net.core.somaxconn). \u5982\u679C\u961F\u5217\u672A\u6EE1,\u5219\u653E\u8FDB\u53BB, \u72B6\u6001\u53D8\u4E3A ESTABLISHED. \u7CFB\u7EDF\u8C03\u7528accept\u5C31\u662F\u4ECE\u8FD9\u4E2A\u5168\u961F\u5217\u53D6\u5DF2\u7ECF\u4E09\u6B21\u63E1\u624B\u6210\u529F\u7684socket<br> \u5982\u679C\u5168\u961F\u5217\u6EE1:<br> \u5F53net.ipv4.tcp_abort_on_overflow\u4E3A0, \u5219\u4E22\u5F03ack, \u5047\u5B9A\u81EA\u5DF1\u6CA1\u6709\u6536\u5230ack, \u6240\u4EE5\u91CD\u53D1syn+ack, \u91CD\u8BD5\u6B21\u6570\u4E3Anet.ipv4.tcp_synack_retries.<br> \u5F53net.ipv4.tcp_abort_on_overflow\u4E3A1, \u5219\u6536\u5230ack\u540E\u76F4\u63A5\u53D1rst\u7EC8\u6B62\u8FDE\u63A5</li><li>\u5148close socket\u7684\u4F1A\u8FDB\u5165time-wait\u72B6\u6001, \u8FD9\u4E2A\u8FC72MSL\u4F1A\u81EA\u52A8\u6D88\u5931.<br> client\u548Cserver\u540C\u65F6\u6253\u5F00\u65F6\u95F4\u6233\u65F6: <ul><li>net.ipv4.tcp_tw_recycle = 1, \u53EF\u5FEB\u901F\u56DE\u6536\u8FDB\u5165time-wait\u72B6\u6001\u7684socket. \u5BF9\u5BA2\u6237\u7AEF\u548C\u670D\u52A1\u7AEF\u90FD\u6709\u6548. \u4F46\u5728nat\u7F51\u7EDC\u4E0B\u53EF\u80FD\u4F1A\u5C06\u6B63\u5E38\u7684syn\u62A5\u6587\u4E22\u5F03</li><li>net.ipv4.tcp_tw_reuse = 1, \u53EF\u5FEB\u901F\u91CD\u7528time-wait\u7684socket. \u53EA\u5BF9\u5BA2\u6237\u7AEF\u6709\u6548.\u4F5C\u4E3A\u5BA2\u6237\u7AEF\u56E0\u4E3A\u6709\u7AEF\u53E365535\u95EE\u9898\uFF0CTIME_OUT\u8FC7\u591A\u76F4\u63A5\u5F71\u54CD\u5904\u7406\u80FD\u529B\uFF0C\u6253\u5F00tw_reuse \u5373\u53EF\u89E3\u51B3</li><li>tcp_max_tw_buckets\u8BBE\u7F6Etime-wait\u7684\u4E0A\u9650, \u4E00\u65E6\u8D85\u8FC7OS\u4F1A\u7ACB\u5373\u6E05\u9664.</li></ul></li><li>\u5F53\u6536\u5230fin\u540E, \u56DE\u590Dack\u540E socket\u8FDB\u5165close-wait\u72B6\u6001. \u6B64\u65F6\u9700\u8981\u7A0B\u5E8F\u8FD0\u884Cclose\u624D\u4F1A\u4E0B\u53D1fin\u62A5\u6587. \u5B8C\u62104\u6B21\u5173\u95ED</li><li>\u5047\u8BBE\u53D1\u9001fin\u540E, \u6CA1\u6709\u6536\u5230\u5BF9\u7AEF\u7684fin, \u5219socket\u8FDB\u5165 fin-wait-2\u72B6\u6001. \u5982\u679C\u4E00\u76F4\u6CA1\u6709\u6536\u5230\u5BF9\u7AEF\u7684fin, \u5219\u7ECF\u8FC7<code>net.ipv4.tcp_fin_timeout</code>\u79D2\u540E\u81EA\u52A8\u6D88\u5931</li></ol><h3 id="tcp\u534A\u961F\u5217\u548C\u5168\u961F\u5217\u76D1\u63A7" tabindex="-1">TCP\u534A\u961F\u5217\u548C\u5168\u961F\u5217\u76D1\u63A7 <a class="header-anchor" href="#tcp\u534A\u961F\u5217\u548C\u5168\u961F\u5217\u76D1\u63A7" aria-hidden="true">#</a></h3><p>\u534A\u961F\u5217\u5982\u679C\u4E22\u5305, \u53EF\u68C0\u67E5\u5982\u4E0B\u503C\u662F\u5426\u589E\u957F:<br><code>netstat -s</code> \u89C2\u5BDF<code>SYNs to LISTEN sockets dropped</code><br><code>nstat</code> \u89C2\u5BDF <code>ListenDrops</code></p><p>\u5168\u961F\u5217\u5982\u679C\u4E22\u5305, \u53EF\u68C0\u67E5\u5982\u4E0B\u503C\u662F\u5426\u589E\u957F:<br><code>netstat -s</code> \u89C2\u5BDF<code>times the listen queue of a socket overflowed</code><br><code>nstat</code> \u89C2\u5BDF <code>TcpExtListenOverflows</code><br> \u4E24\u4E2A\u5DE5\u5177\u7684\u4FE1\u606F\u90FD\u53D6\u81EA<code>/proc/net/netstat</code></p><h3 id="tcp\u4E22\u5305\u4E00\u4E9B\u6392\u67E5\u65B9\u6CD5" tabindex="-1">TCP\u4E22\u5305\u4E00\u4E9B\u6392\u67E5\u65B9\u6CD5 <a class="header-anchor" href="#tcp\u4E22\u5305\u4E00\u4E9B\u6392\u67E5\u65B9\u6CD5" aria-hidden="true">#</a></h3><p>\u4F7F\u7528perf \u8DDF\u8E2A skb:kfree_skb, \u7136\u540E\u770B\u5806\u6808<br><a href="https://jvns.ca/blog/2017/09/05/finding-out-where-packets-are-being-dropped/" target="_blank" rel="noopener noreferrer">https://jvns.ca/blog/2017/09/05/finding-out-where-packets-are-being-dropped/</a></p><h3 id="net-ipv4-tcp-tw-recycle-1" tabindex="-1">net.ipv4.tcp_tw_recycle = 1 <a class="header-anchor" href="#net-ipv4-tcp-tw-recycle-1" aria-hidden="true">#</a></h3><p>\u5728nat\u573A\u666F\u4E0B\u4F1A\u4EA7\u751F\u4E22\u5305. \u9AD8\u5185\u6838\u5DF2\u7ECF\u5220\u9664\u8FD9\u4E2A\u53C2\u6570<br><a href="https://github.com/torvalds/linux/commit/4396e46187ca5070219b81773c4e65088dac50cc" target="_blank" rel="noopener noreferrer">https://github.com/torvalds/linux/commit/4396e46187ca5070219b81773c4e65088dac50cc</a><br> \u5BF9\u5E94\u7684\u8BA1\u6570\u9879\u4E3A<code>TcpExtPAWSPassive</code></p><h3 id="tcp\u90E8\u5206\u53C2\u6570\u4F7F\u7528\u4ECB\u7ECD" tabindex="-1">tcp\u90E8\u5206\u53C2\u6570\u4F7F\u7528\u4ECB\u7ECD <a class="header-anchor" href="#tcp\u90E8\u5206\u53C2\u6570\u4F7F\u7528\u4ECB\u7ECD" aria-hidden="true">#</a></h3><p>tcp keepalive\u9ED8\u8BA4\u4E00\u4E9B\u53C2\u6570\u5982\u4E0B, \u5E94\u7528\u7A0B\u5E8F\u91CC\u4E5F\u53EF\u4EE5\u4FEE\u6539. enable\u8BE5\u7279\u6027\u5FC5\u987B\u662F\u5728\u7A0B\u5E8F\u91CC\u8BBE\u7F6E. OS\u6CA1\u6709\u5168\u90E8\u7684\u6253\u5F00\u8BE5\u529F\u80FD\u7684\u53C2\u6570</p><div class="language-bash"><span class="copy"></span><pre><code><span class="line"><span style="color:#A6ACCD;">$ sysctl -a </span><span style="color:#89DDFF;">|</span><span style="color:#A6ACCD;"> grep keep</span></span>
<span class="line"><span style="color:#A6ACCD;">net.ipv4.tcp_keepalive_intvl = 75</span></span>
<span class="line"><span style="color:#A6ACCD;">net.ipv4.tcp_keepalive_probes = 9</span></span>
<span class="line"><span style="color:#A6ACCD;">net.ipv4.tcp_keepalive_time = 7200</span></span>
<span class="line"></span></code></pre></div><p>Nagle\u7B97\u6CD5\u4E3B\u8981\u5728\u6570\u636E\u5305\u5F88\u5C0F\u5F88\u5C0F\u65F6,\u5148\u4E0D\u53D1,\u7B49\u7D2F\u79EF\u5230\u4E00\u5B9A\u7A0B\u5EA6\u518D\u53D1\u51FA\u53BB.\u9ED8\u8BA4\u662F\u6253\u5F00\u7684.\u5E94\u7528\u7A0B\u5E8F\u53EF\u4EE5\u901A\u8FC7TCP_NODELAY \u9009\u9879\u6765\u5173\u95ED\u8FD9\u4E2A\u7B97\u6CD5, OS\u6CA1\u6709\u5168\u5C40\u7684\u5173\u95ED\u8BE5\u7B97\u6CD5\u7684\u53C2\u6570</p><p>TCP\u7684\u5EF6\u8FDF\u786E\u8BA4\u7684\u7B56\u7565\uFF1A<br> \u5F53\u6709\u54CD\u5E94\u6570\u636E\u8981\u53D1\u9001\u65F6\uFF0CACK \u4F1A\u968F\u7740\u54CD\u5E94\u6570\u636E\u4E00\u8D77\u7ACB\u523B\u53D1\u9001\u7ED9\u5BF9\u65B9<br> \u5F53\u6CA1\u6709\u54CD\u5E94\u6570\u636E\u8981\u53D1\u9001\u65F6\uFF0CACK \u5C06\u4F1A\u5EF6\u8FDF\u4E00\u6BB5\u65F6\u95F4\uFF0C\u4EE5\u7B49\u5F85\u662F\u5426\u6709\u54CD\u5E94\u6570\u636E\u53EF\u4EE5\u4E00\u8D77\u53D1\u9001<br> \u5982\u679C\u5728\u5EF6\u8FDF\u7B49\u5F85\u53D1\u9001 ACK \u671F\u95F4\uFF0C\u5BF9\u65B9\u7684\u7B2C\u4E8C\u4E2A\u6570\u636E\u62A5\u6587\u53C8\u5230\u8FBE\u4E86\uFF0C\u8FD9\u65F6\u5C31\u4F1A\u7ACB\u523B\u53D1\u9001 ACK</p><p>\u6700\u5927\u5EF6\u8FDF\u786E\u8BA4\u65F6\u95F4\u662F 200 ms \uFF081000/5\uFF09<br> \u6700\u77ED\u5EF6\u8FDF\u786E\u8BA4\u65F6\u95F4\u662F 40 ms \uFF081000/25\uFF09</p><p>tcp_retries2 \u6307\u5B9A\u8D85\u65F6\u91CD\u4F20\u7684\u6B21\u6570, \u9ED8\u8BA4\u662F15\u6B21, \u5927\u6982\u662F16\u5206\u949F\u5DE6\u53F3</p><p>\u6BD4\u5982\u5DF2\u7ECF\u5EFA\u7ACB\u7684TCP\u8FDE\u63A5, \u56E0\u67D0\u4E9B\u539F\u56E0(\u9632\u706B\u5899,\u4E2D\u95F4\u7F51\u7EDC\u4E2D\u65AD)\u5BFC\u81F4\u53D1\u5F80\u53E6\u4E00\u65B9\u7684\u5305, \u4E00\u76F4\u6CA1\u6709\u6536\u5230\u56DE\u5305. \u8FD9\u4E2ARTO\u8D85\u65F6\u540E\u5F00\u59CB\u91CD\u4F20.<br> RTO\u6700\u5C0F\u662F 200ms, \u6700\u5927\u662F 2\u5206\u949F. \u6BCF\u6B21\u53D1\u8D77\u91CD\u4F20\u7684\u95F4\u9694\u4E3A 200ms, 400ms, 800ms \u9010\u6E10\u9012\u589E.</p><h1 id="\u53C2\u8003" tabindex="-1">\u53C2\u8003: <a class="header-anchor" href="#\u53C2\u8003" aria-hidden="true">#</a></h1><p><a href="https://www.kernel.org/doc/Documentation/networking/ip-sysctl.txt" target="_blank" rel="noopener noreferrer">https://www.kernel.org/doc/Documentation/networking/ip-sysctl.txt</a><br><a href="https://www.jianshu.com/p/0d6243402987" target="_blank" rel="noopener noreferrer">https://www.jianshu.com/p/0d6243402987</a><br><a href="https://www.codedump.info/post/20190227-tcp/" target="_blank" rel="noopener noreferrer">https://www.codedump.info/post/20190227-tcp/</a><br><a href="https://blog.packagecloud.io/eng/2016/06/22/monitoring-tuning-linux-networking-stack-receiving-data" target="_blank" rel="noopener noreferrer">https://blog.packagecloud.io/eng/2016/06/22/monitoring-tuning-linux-networking-stack-receiving-data</a><br><a href="https://blog.packagecloud.io/eng/2017/02/06/monitoring-tuning-linux-networking-stack-sending-data" target="_blank" rel="noopener noreferrer">https://blog.packagecloud.io/eng/2017/02/06/monitoring-tuning-linux-networking-stack-sending-data</a><br><a href="https://lwn.net/Articles/362339/" target="_blank" rel="noopener noreferrer">https://lwn.net/Articles/362339/</a><br><a href="https://access.redhat.com/solutions/30453" target="_blank" rel="noopener noreferrer">https://access.redhat.com/solutions/30453</a><br><a href="https://loicpefferkorn.net/2016/03/linux-network-metrics-why-you-should-use-nstat-instead-of-netstat/" target="_blank" rel="noopener noreferrer">https://loicpefferkorn.net/2016/03/linux-network-metrics-why-you-should-use-nstat-instead-of-netstat/</a><br><a href="https://www.kernel.org/doc/Documentation/networking/ip-sysctl.txt" target="_blank" rel="noopener noreferrer">https://www.kernel.org/doc/Documentation/networking/ip-sysctl.txt</a><br><a href="https://perthcharles.github.io/2015/09/07/wiki-tcp-retries/" target="_blank" rel="noopener noreferrer">https://perthcharles.github.io/2015/09/07/wiki-tcp-retries/</a><br><a href="https://pracucci.com/linux-tcp-rto-min-max-and-tcp-retries2.html" target="_blank" rel="noopener noreferrer">https://pracucci.com/linux-tcp-rto-min-max-and-tcp-retries2.html</a></p>`,20),o=[c];function s(i,p,l,d,h,_){return r(),t("div",null,o)}var b=e(a,[["render",s]]);export{g as __pageData,b as default};
